mapper.select:
SELECT COUNT(*) FROM
PRODUCT
WHERE P_NO=#{p_no}

SELECT * FROM (
	SELECT ROWNUM AS RN, A.* FROM (
		SELECT * FROM PRODUCT
		WHERE UPPER(P_NAME) LIKE UPPER('%'||#{sPrd}||'%') OR LOWER(P_NAME) LIKE LOWER('%'||#{sPrd}||'%')
	) A
) WHERE RN BETWEEN #{start} AND #{end}


SELECT * FROM (
	SELECT ROWNUM AS RN, A.* FROM (
		SELECT * FROM PRODUCT WHERE CATE_NO=#{product.cate_no} ORDER BY P_NO DESC
	) A
) WHERE RN BETWEEN #{start} AND #{end}


SELECT CATE_NO, nvl(p_img, 'resource/img/product/') AS P_IMG, P_NAME, P_NO, P_PRICE FROM (
	SELECT ROWNUM AS RN, A.* FROM (
		SELECT * FROM PRODUCT ORDER BY P_NO DESC
	) A
) WHERE RN BETWEEN #{start} AND #{end}
